-- 家常菜
INSERT INTO gourmet (category_id, user_id, title, content, cover, is_audit, is_publish, create_time) VALUES
(1, 1, '红烧肉', '【食材】五花肉500g、冰糖30g、生抽2勺、老抽1勺、料酒2勺、八角2个、桂皮1块、香叶2片、姜片5片、葱段适量\n\n【步骤】\n1. 五花肉切成3厘米见方的块，冷水下锅焯水去血沫，捞出沥干备用。\n2. 锅中放少许油，放入冰糖小火炒出糖色，炒至枣红色冒泡。\n3. 放入五花肉翻炒上色，加入料酒、生抽、老抽翻炒均匀。\n4. 加入八角、桂皮、香叶、姜片、葱段，倒入没过肉的热水。\n5. 大火烧开后转小火炖煮1小时，最后大火收汁即可。\n\n【小贴士】炒糖色时火候要小，糖色炒过了会发苦。', 'https://picsum.photos/400/300?random=1', true, true, NOW()),
(1, 1, '番茄炒蛋', '【食材】鸡蛋3个、番茄2个、葱花适量、盐1勺、糖1勺、食用油适量\n\n【步骤】\n1. 番茄洗净切块，鸡蛋打散加少许盐搅匀。\n2. 锅中倒油烧热，倒入蛋液快速翻炒至凝固盛出。\n3. 锅中留底油，放入番茄块翻炒出汁。\n4. 加入少许盐和糖调味，倒入炒好的鸡蛋翻炒均匀。\n5. 撒上葱花即可出锅。\n\n【小贴士】番茄要选熟透的，炒出来汁水更多更甜。', 'https://picsum.photos/400/300?random=2', true, true, NOW()),
(1, 1, '可乐鸡翅', '【食材】鸡翅中10个、可乐1罐、生抽2勺、老抽1勺、料酒1勺、姜片5片、葱段适量\n\n【步骤】\n1. 鸡翅洗净，两面划几刀方便入味。\n2. 冷水下锅焯水去血沫，捞出沥干。\n3. 锅中放少许油，放入鸡翅煎至两面金黄。\n4. 加入姜片、葱段、料酒、生抽、老抽翻炒上色。\n5. 倒入可乐没过鸡翅，大火烧开转小火炖20分钟。\n6. 最后大火收汁，汤汁浓稠即可。\n\n【小贴士】收汁时要不停翻动，防止糊锅。', 'https://picsum.photos/400/300?random=3', true, true, NOW()),
(1, 1, '蒜蓉西兰花', '【食材】西兰花1颗、大蒜5瓣、盐1勺、蚝油1勺、食用油适量\n\n【步骤】\n1. 西兰花掰成小朵，淡盐水浸泡10分钟，洗净沥干。\n2. 大蒜切成蒜蓉备用。\n3. 锅中烧开水，放入西兰花焯水1分钟，捞出过凉水。\n4. 锅中倒油烧热，放入蒜蓉爆香。\n5. 放入西兰花翻炒，加入盐和蚝油调味，翻炒均匀即可。\n\n【小贴士】焯水时间不要太长，保持西兰花脆嫩口感。', 'https://picsum.photos/400/300?random=4', true, true, NOW()),
(1, 1, '酸辣土豆丝', '【食材】土豆2个、干辣椒5个、花椒适量、蒜末适量、醋2勺、盐1勺、葱花适量\n\n【步骤】\n1. 土豆去皮切丝，用清水冲洗两遍去除淀粉，沥干水分。\n2. 干辣椒剪段，蒜切末，葱切葱花。\n3. 锅中倒油烧热，放入花椒、干辣椒、蒜末爆香。\n4. 放入土豆丝大火快速翻炒至断生。\n5. 加入醋、盐调味，撒上葱花即可出锅。\n\n【小贴士】土豆丝要切得均匀，大火快炒保持脆爽口感。', 'https://picsum.photos/400/300?random=5', true, true, NOW()),

-- 川菜
(2, 1, '麻婆豆腐', '【食材】嫩豆腐1块、牛肉末100g、豆瓣酱2勺、花椒粉1勺、辣椒面1勺、蒜末适量、葱花适量、生抽1勺、水淀粉适量\n\n【步骤】\n1. 豆腐切成2厘米见方的块，淡盐水浸泡备用。\n2. 锅中放油，放入牛肉末炒散变色。\n3. 加入豆瓣酱炒出红油，放入蒜末爆香。\n4. 加入适量水，放入豆腐块，加生抽调味。\n5. 中火煮3分钟，淋入水淀粉勾芡。\n6. 撒上花椒粉、辣椒面和葱花即可。\n\n【小贴士】豆腐提前用盐水泡可以去除豆腥味，不易碎。', 'https://picsum.photos/400/300?random=6', true, true, NOW()),
(2, 1, '宫保鸡丁', '【食材】鸡胸肉300g、花生米50g、干辣椒10个、花椒适量、葱段适量、蒜末适量、生抽2勺、醋1勺、糖1勺、料酒1勺、淀粉适量\n\n【步骤】\n1. 鸡胸肉切丁，加料酒、盐、淀粉腌制15分钟。\n2. 花生米炸至酥脆备用。\n3. 碗中调好料汁：生抽、醋、糖、淀粉、少许水。\n4. 锅中放油，放入鸡丁滑炒至变色盛出。\n5. 锅中留底油，放入干辣椒、花椒、蒜末爆香。\n6. 放入鸡丁翻炒，倒入料汁炒匀，最后放入花生米和葱段即可。\n\n【小贴士】花生米最后放，保持酥脆口感。', 'https://picsum.photos/400/300?random=7', true, true, NOW()),
(2, 1, '水煮鱼', '【食材】草鱼1条、豆芽200g、豆瓣酱2勺、干辣椒20个、花椒适量、蒜末适量、蛋清1个、淀粉适量、盐适量\n\n【步骤】\n1. 草鱼处理干净，片成鱼片，加蛋清、淀粉、盐腌制。\n2. 豆芽焯水铺在碗底。\n3. 锅中放油，放入豆瓣酱炒出红油，加入适量水烧开。\n4. 放入鱼骨煮5分钟，再放入鱼片煮至变色。\n5. 将鱼和汤倒入碗中，撒上蒜末、干辣椒、花椒。\n6. 淋上热油激发香味即可。\n\n【小贴士】鱼片不要煮太久，变色即可，保持嫩滑。', 'https://picsum.photos/400/300?random=8', true, true, NOW()),
(2, 1, '回锅肉', '【食材】五花肉300g、青蒜2根、豆瓣酱2勺、甜面酱1勺、生抽1勺、料酒1勺、姜片适量\n\n【步骤】\n1. 五花肉冷水下锅，加姜片、料酒煮至八成熟，捞出晾凉。\n2. 五花肉切薄片，青蒜切段。\n3. 锅中不放油，放入五花肉片煸炒出油。\n4. 加入豆瓣酱、甜面酱炒出红油。\n5. 放入青蒜翻炒至断生，加生抽调味即可。\n\n【小贴士】五花肉要煮到八成熟，太生太熟都不好切片。', 'https://picsum.photos/400/300?random=9', true, true, NOW()),
(2, 1, '鱼香肉丝', '【食材】猪里脊肉200g、木耳50g、胡萝卜半根、青椒1个、豆瓣酱1勺、醋2勺、糖1勺、生抽1勺、淀粉适量\n\n【步骤】\n1. 猪里脊切丝，加淀粉、料酒腌制。木耳、胡萝卜、青椒切丝。\n2. 碗中调鱼香汁：醋、糖、生抽、淀粉、少许水。\n3. 锅中放油，放入肉丝滑炒至变色盛出。\n4. 锅中留底油，放入豆瓣酱炒出红油。\n5. 放入木耳、胡萝卜、青椒翻炒，再放入肉丝。\n6. 倒入鱼香汁炒匀即可。\n\n【小贴士】鱼香汁的酸甜比例可以根据个人口味调整。', 'https://picsum.photos/400/300?random=10', true, true, NOW()),

-- 粤菜
(3, 1, '白切鸡', '【食材】三黄鸡1只、姜片5片、葱段适量、料酒2勺、蘸料：姜蓉、葱花、盐、热油\n\n【步骤】\n1. 三黄鸡处理干净，冷水下锅，放入姜片、葱段、料酒。\n2. 大火烧开后转小火，保持微沸状态煮15分钟。\n3. 关火焖15分钟，取出放入冰水中浸泡至冷却。\n4. 鸡肉斩块摆盘。\n5. 姜蓉、葱花加盐，淋上热油制成蘸料。\n\n【小贴士】冰水浸泡可以让鸡皮更加爽脆。', 'https://picsum.photos/400/300?random=11', true, true, NOW()),
(3, 1, '蒜蓉粉丝蒸扇贝', '【食材】扇贝6个、粉丝1把、大蒜1头、葱花适量、生抽2勺、蚝油1勺\n\n【步骤】\n1. 粉丝用温水泡软，剪短备用。\n2. 扇贝洗净，肉和壳分离，壳刷干净。\n3. 大蒜切成蒜蓉，用油炸至金黄。\n4. 粉丝铺在扇贝壳上，放上扇贝肉。\n5. 淋上蒜蓉、生抽、蚝油。\n6. 上锅蒸5分钟，撒上葱花即可。\n\n【小贴士】蒜蓉炸一下更香，蒸的时间不宜过长。', 'https://picsum.photos/400/300?random=12', true, true, NOW()),
(3, 1, '叉烧肉', '【食材】猪梅花肉500g、叉烧酱3勺、蜂蜜2勺、生抽1勺、料酒1勺\n\n【步骤】\n1. 猪梅花肉切条，用叉子扎些小孔方便入味。\n2. 加入叉烧酱、蜂蜜、生抽、料酒腌制4小时以上。\n3. 烤箱预热200度，肉放烤架上。\n4. 烤20分钟，翻面刷腌料再烤15分钟。\n5. 取出切片即可食用。\n\n【小贴士】腌制时间越长越入味，可以提前一晚腌制。', 'https://picsum.photos/400/300?random=13', true, true, NOW()),
(3, 1, '广式蒸排骨', '【食材】排骨400g、豆豉1勺、蒜末适量、生抽2勺、蚝油1勺、淀粉1勺、糖少许\n\n【步骤】\n1. 排骨洗净斩小块，用清水浸泡去血水。\n2. 沥干水分，加入豆豉、蒜末、生抽、蚝油、糖、淀粉抓匀。\n3. 腌制30分钟入味。\n4. 装盘，上锅大火蒸20分钟。\n5. 撒上葱花即可。\n\n【小贴士】排骨要选肋排，肉质更嫩。', 'https://picsum.photos/400/300?random=14', true, true, NOW()),
(3, 1, '蚝油生菜', '【食材】生菜2颗、大蒜3瓣、蚝油2勺、生抽1勺、淀粉少许、食用油适量\n\n【步骤】\n1. 生菜洗净沥干，大蒜切末。\n2. 锅中烧开水，放入生菜焯水30秒，捞出沥干摆盘。\n3. 锅中放油，放入蒜末爆香。\n4. 加入蚝油、生抽、少许水烧开。\n5. 淋入水淀粉勾芡，浇在生菜上即可。\n\n【小贴士】生菜焯水时间要短，保持脆嫩。', 'https://picsum.photos/400/300?random=15', true, true, NOW()),

-- 烘焙
(4, 1, '戚风蛋糕', '【食材】鸡蛋4个、低筋面粉80g、细砂糖60g、玉米油40g、牛奶40g、柠檬汁几滴\n\n【步骤】\n1. 蛋黄蛋白分离，蛋黄加玉米油、牛奶搅匀。\n2. 筛入低筋面粉，Z字形搅拌至无颗粒。\n3. 蛋白加柠檬汁，分三次加糖打发至干性发泡。\n4. 取1/3蛋白入蛋黄糊翻拌，再倒回蛋白中翻拌均匀。\n5. 倒入模具，震出气泡，150度烤50分钟。\n6. 出炉倒扣晾凉脱模。\n\n【小贴士】翻拌手法要轻，避免消泡。', 'https://picsum.photos/400/300?random=16', true, true, NOW()),
(4, 1, '蔓越莓饼干', '【食材】低筋面粉115g、黄油75g、糖粉45g、蛋液15g、蔓越莓干35g\n\n【步骤】\n1. 黄油软化，加糖粉打发至蓬松。\n2. 分次加入蛋液搅打均匀。\n3. 筛入低筋面粉，加入切碎的蔓越莓干。\n4. 拌成面团，整形成长方体，冷冻1小时。\n5. 切成0.7cm厚的片，165度烤15分钟。\n\n【小贴士】面团要冷冻定型，切片才整齐。', 'https://picsum.photos/400/300?random=17', true, true, NOW()),
(4, 1, '蛋挞', '【食材】蛋挞皮10个、淡奶油100g、牛奶80g、蛋黄2个、细砂糖25g\n\n【步骤】\n1. 淡奶油、牛奶、糖混合加热至糖融化。\n2. 晾凉后加入蛋黄搅匀，过筛备用。\n3. 蛋挞皮摆入烤盘，倒入蛋挞液七八分满。\n4. 烤箱预热200度，烤20分钟至表面有焦糖色。\n\n【小贴士】蛋挞液过筛口感更细腻。', 'https://picsum.photos/400/300?random=18', true, true, NOW()),
(4, 1, '纸杯蛋糕', '【食材】低筋面粉100g、鸡蛋2个、细砂糖60g、黄油50g、泡打粉3g、牛奶30g\n\n【步骤】\n1. 黄油软化，加糖打发至蓬松发白。\n2. 分次加入蛋液搅打均匀。\n3. 筛入低筋面粉和泡打粉，加牛奶翻拌均匀。\n4. 装入裱花袋，挤入纸杯七分满。\n5. 170度烤20分钟至表面金黄。\n\n【小贴士】面糊不要装太满，烤时会膨胀。', 'https://picsum.photos/400/300?random=19', true, true, NOW()),
(4, 1, '吐司面包', '【食材】高筋面粉250g、牛奶160g、酵母3g、细砂糖30g、盐3g、黄油25g\n\n【步骤】\n1. 除黄油外所有材料揉成团，加入黄油揉至扩展阶段。\n2. 放温暖处发酵至2倍大。\n3. 排气分成三份，擀卷两次，放入吐司盒。\n4. 二次发酵至八分满。\n5. 180度烤35分钟，出炉脱模晾凉。\n\n【小贴士】揉面要出膜，面包才会松软。', 'https://picsum.photos/400/300?random=20', true, true, NOW()),

-- 汤品
(5, 1, '番茄牛腩汤', '【食材】牛腩500g、番茄3个、土豆1个、胡萝卜1根、番茄酱2勺、姜片适量、葱段适量\n\n【步骤】\n1. 牛腩切块，冷水下锅焯水去血沫。\n2. 番茄切块，土豆、胡萝卜切块。\n3. 锅中放油，放入番茄炒出汁，加番茄酱。\n4. 加入牛腩、姜片、葱段，倒入足量水。\n5. 大火烧开转小火炖1.5小时。\n6. 加入土豆、胡萝卜再炖30分钟，调味即可。\n\n【小贴士】番茄先炒出汁，汤更浓郁。', 'https://picsum.photos/400/300?random=21', true, true, NOW()),
(5, 1, '冬瓜排骨汤', '【食材】排骨400g、冬瓜500g、姜片5片、葱段适量、盐适量、枸杞少许\n\n【步骤】\n1. 排骨冷水下锅焯水去血沫，捞出洗净。\n2. 冬瓜去皮切块。\n3. 排骨放入砂锅，加姜片、葱段和足量水。\n4. 大火烧开转小火炖1小时。\n5. 放入冬瓜继续炖20分钟。\n6. 加盐调味，撒上枸杞即可。\n\n【小贴士】冬瓜后放，保持形状不烂。', 'https://picsum.photos/400/300?random=22', true, true, NOW()),
(5, 1, '银耳莲子汤', '【食材】银耳1朵、莲子50g、红枣10颗、枸杞适量、冰糖适量\n\n【步骤】\n1. 银耳提前泡发，撕成小朵。\n2. 莲子去芯，红枣洗净。\n3. 银耳放入砂锅，加足量水。\n4. 大火烧开转小火炖1小时至出胶。\n5. 加入莲子、红枣继续炖30分钟。\n6. 加冰糖调味，撒枸杞即可。\n\n【小贴士】银耳要炖出胶才好喝。', 'https://picsum.photos/400/300?random=23', true, true, NOW()),
(5, 1, '酸辣汤', '【食材】豆腐100g、木耳50g、鸡蛋1个、醋2勺、胡椒粉1勺、淀粉适量、香菜适量\n\n【步骤】\n1. 豆腐、木耳切丝。\n2. 锅中加水烧开，放入豆腐、木耳煮2分钟。\n3. 加醋、胡椒粉、生抽调味。\n4. 淋入水淀粉勾芡，再淋入蛋液。\n5. 搅拌成蛋花，撒上香菜即可。\n\n【小贴士】先勾芡再淋蛋液，蛋花更漂亮。', 'https://picsum.photos/400/300?random=24', true, true, NOW()),
(5, 1, '玉米排骨汤', '【食材】排骨400g、玉米2根、胡萝卜1根、姜片适量、盐适量\n\n【步骤】\n1. 排骨冷水下锅焯水去血沫。\n2. 玉米切段，胡萝卜切块。\n3. 排骨放入砂锅，加姜片和足量水。\n4. 大火烧开转小火炖1小时。\n5. 放入玉米、胡萝卜继续炖30分钟。\n6. 加盐调味即可。\n\n【小贴士】玉米选糯玉米，汤更甜。', 'https://picsum.photos/400/300?random=25', true, true, NOW()),

-- 凉菜
(6, 1, '凉拌黄瓜', '【食材】黄瓜2根、蒜末适量、小米椒2个、醋2勺、生抽1勺、香油适量、盐适量\n\n【步骤】\n1. 黄瓜洗净拍碎，切段。\n2. 加盐腌制10分钟，倒掉水分。\n3. 加入蒜末、小米椒圈。\n4. 加醋、生抽、香油拌匀即可。\n\n【小贴士】拍碎的黄瓜更入味。', 'https://picsum.photos/400/300?random=26', true, true, NOW()),
(6, 1, '凉拌木耳', '【食材】木耳100g、蒜末适量、小米椒2个、醋2勺、生抽1勺、香油适量、香菜适量\n\n【步骤】\n1. 木耳提前泡发，焯水2分钟。\n2. 过凉水沥干。\n3. 加入蒜末、小米椒、香菜。\n4. 加醋、生抽、香油拌匀即可。\n\n【小贴士】木耳焯水后过凉水更脆。', 'https://picsum.photos/400/300?random=27', true, true, NOW()),
(6, 1, '皮蛋豆腐', '【食材】嫩豆腐1块、皮蛋2个、蒜末适量、生抽2勺、香油适量、葱花适量\n\n【步骤】\n1. 嫩豆腐切块摆盘。\n2. 皮蛋切块放在豆腐上。\n3. 撒上蒜末、葱花。\n4. 淋上生抽、香油即可。\n\n【小贴士】豆腐可以先用淡盐水泡一下。', 'https://picsum.photos/400/300?random=28', true, true, NOW()),
(6, 1, '凉拌海带丝', '【食材】海带丝200g、蒜末适量、醋2勺、生抽1勺、辣椒油适量、芝麻适量\n\n【步骤】\n1. 海带丝焯水2分钟，过凉水沥干。\n2. 加入蒜末、醋、生抽、辣椒油。\n3. 撒上芝麻拌匀即可。\n\n【小贴士】海带丝焯水时间不宜过长。', 'https://picsum.photos/400/300?random=29', true, true, NOW()),
(6, 1, '口水鸡', '【食材】鸡腿2个、花生碎适量、蒜末适量、辣椒油2勺、花椒油1勺、生抽2勺、醋1勺、糖少许、芝麻适量\n\n【步骤】\n1. 鸡腿冷水下锅煮熟，过冰水。\n2. 去骨切块摆盘。\n3. 碗中调汁：蒜末、辣椒油、花椒油、生抽、醋、糖。\n4. 淋在鸡肉上，撒上花生碎、芝麻、葱花。\n\n【小贴士】过冰水让鸡皮更爽脆。', 'https://picsum.photos/400/300?random=30', true, true, NOW()),

-- 主食
(7, 1, '蛋炒饭', '【食材】米饭1碗、鸡蛋2个、葱花适量、盐适量、食用油适量\n\n【步骤】\n1. 鸡蛋打散，米饭打散备用。\n2. 锅中倒油，倒入蛋液快速翻炒盛出。\n3. 锅中再倒油，放入米饭翻炒至粒粒分明。\n4. 加入炒好的鸡蛋，加盐调味。\n5. 撒上葱花即可。\n\n【小贴士】米饭要用隔夜饭，更干爽。', 'https://picsum.photos/400/300?random=31', true, true, NOW()),
(7, 1, '葱油拌面', '【食材】面条200g、小葱5根、生抽2勺、老抽1勺、糖少许、食用油适量\n\n【步骤】\n1. 小葱切段。\n2. 锅中倒油，放入葱段小火炸至金黄。\n3. 碗中放生抽、老抽、糖，倒入葱油。\n4. 面条煮熟捞出，拌入葱油汁即可。\n\n【小贴士】葱段要炸到金黄酥脆才香。', 'https://picsum.photos/400/300?random=32', true, true, NOW()),
(7, 1, '饺子', '【食材】饺子皮适量、猪肉馅300g、韭菜200g、生抽2勺、料酒1勺、盐适量、香油适量\n\n【步骤】\n1. 韭菜切碎，猪肉馅加调料搅打上劲。\n2. 加入韭菜、香油拌匀成馅。\n3. 取饺子皮包入馅料，捏紧收口。\n4. 锅中烧开水，放入饺子煮至浮起。\n5. 加一次凉水，再次煮开即可。\n\n【小贴士】煮饺子加凉水可以让皮更劲道。', 'https://picsum.photos/400/300?random=33', true, true, NOW()),
(7, 1, '手抓饼', '【食材】面粉300g、温水180g、食用油适量、葱花适量、盐适量\n\n【步骤】\n1. 面粉加温水和成面团，醒30分钟。\n2. 擀成薄片，刷油、撒葱花、盐。\n3. 卷起盘成圆形，再擀薄。\n4. 平底锅刷油，煎至两面金黄。\n5. 用铲子打散即可。\n\n【小贴士】面团要软，饼才酥脆。', 'https://picsum.photos/400/300?random=34', true, true, NOW()),
(7, 1, '牛肉面', '【食材】面条200g、牛肉200g、青菜适量、八角2个、桂皮1块、生抽2勺、老抽1勺、姜片适量\n\n【步骤】\n1. 牛肉切块焯水，放入锅中加香料炖煮1.5小时。\n2. 加生抽、老抽调味。\n3. 面条煮熟捞出，放入碗中。\n4. 浇上牛肉汤，放上牛肉块和烫熟的青菜。\n5. 撒上葱花即可。\n\n【小贴士】牛肉要炖烂，汤才浓郁。', 'https://picsum.photos/400/300?random=35', true, true, NOW()),

-- 甜品
(8, 1, '双皮奶', '【食材】牛奶500ml、蛋清3个、细砂糖30g\n\n【步骤】\n1. 牛奶加热至微沸，倒入碗中晾凉结皮。\n2. 倒出牛奶，碗底留一层奶皮。\n3. 牛奶加糖、蛋清搅匀，过筛。\n4. 倒回碗中，奶皮浮起。\n5. 上锅蒸15分钟，晾凉即可。\n\n【小贴士】牛奶要晾凉才能结皮。', 'https://picsum.photos/400/300?random=36', true, true, NOW()),
(8, 1, '芒果班戟', '【食材】低筋面粉50g、鸡蛋2个、牛奶150g、糖20g、黄油10g、淡奶油200g、芒果适量\n\n【步骤】\n1. 鸡蛋加糖搅匀，加牛奶、融化的黄油。\n2. 筛入面粉搅匀，过筛成面糊。\n3. 平底锅摊成薄饼。\n4. 淡奶油打发，芒果切块。\n5. 饼皮铺开，放奶油和芒果，包成方形。\n\n【小贴士】饼皮要薄，包起来才好看。', 'https://picsum.photos/400/300?random=37', true, true, NOW()),
(8, 1, '杨枝甘露', '【食材】芒果2个、西米50g、椰浆200ml、牛奶100ml、糖适量\n\n【步骤】\n1. 西米煮至透明，过凉水备用。\n2. 芒果切块，一部分打成泥。\n3. 碗中放入西米、芒果块。\n4. 倒入椰浆、牛奶，加糖调味。\n5. 放入芒果泥即可。\n\n【小贴士】西米要煮到完全透明。', 'https://picsum.photos/400/300?random=38', true, true, NOW()),
(8, 1, '焦糖布丁', '【食材】鸡蛋3个、牛奶250ml、细砂糖60g（焦糖用30g+布丁用30g）、水15g\n\n【步骤】\n1. 30g糖加水熬成焦糖，倒入模具底部。\n2. 牛奶加30g糖加热至糖融化。\n3. 鸡蛋打散，倒入牛奶搅匀，过筛。\n4. 倒入模具，上锅蒸20分钟。\n5. 晾凉脱模即可。\n\n【小贴士】熬焦糖要用小火，颜色深了会苦。', 'https://picsum.photos/400/300?random=39', true, true, NOW()),
(8, 1, '红豆沙', '【食材】红豆200g、冰糖适量、陈皮1片\n\n【步骤】\n1. 红豆提前浸泡4小时。\n2. 放入砂锅，加水和陈皮。\n3. 大火烧开转小火炖1.5小时。\n4. 加冰糖调味，继续炖至软烂。\n5. 可以直接吃或做其他甜品馅料。\n\n【小贴士】加陈皮可以去除豆腥味。', 'https://picsum.photos/400/300?random=40', true, true, NOW());

