mapper.update:
UPDATE evaluations
parent_id = #{parentId}, commenter_id = #{commenterId}, replier_id = #{replierId}, content_type = #{contentType}, content_id = #{contentId}, content = #{content}, create_time = #{createTime},
WHERE id = #{id}

mapper.select:
SELECT e.id AS id, e.content AS content, e.create_time AS createTime, e.parent_id AS parentId, e.content_type AS contentType, u.id AS userId, u.user_name AS userName, (SELECT COUNT(*) FROM evaluations_upvote eu WHERE eu.user_id = #{userId} AND eu.evaluations_id = e.id) AS upvoteFlag, (SELECT COUNT(*) FROM evaluations_upvote eu WHERE eu.evaluations_id = e.id) AS upvoteCount, u.user_avatar AS userAvatar, u1.id AS replier_id, u1.user_name AS replierName, u1.user_avatar AS replierAvatar FROM evaluations e LEFT JOIN user u ON u.id = e.commenter_id LEFT JOIN user u1 ON u1.id = e.replier_id
AND e.content_type = #{contentType}
AND e.content like concat('%',#{content},'%')
AND e.create_time BETWEEN #{startTime} AND #{endTime}
ORDER BY e.create_time DESC
LIMIT #{current},#{size}

SELECT COUNT(*)
FROM evaluations e
WHERE e.content_id = #{contentId}
  AND e.content_type = #{contentType}

SELECT COUNT(*)
FROM evaluations e
WHERE e.commenter_id != #{userId}
  AND (
      EXISTS (
          SELECT 1 FROM gourmet g
          WHERE g.id = e.content_id
            AND g.user_id = #{userId}
            AND e.content_type = 'gourmet'
      )
      OR
      EXISTS (
          SELECT 1 FROM content_net cn
          WHERE cn.id = e.content_id
            AND cn.user_id = #{userId}
            AND e.content_type = 'content_net'
      )
  )
