mapper.delete:
DELETE
FROM interaction
WHERE type = #{type}
  AND content_id = #{contentId}
  AND user_id = #{userId}

mapper.select:
SELECT i.*,
g.title AS content_title,
u.user_name,
u.user_avatar
FROM interaction i
LEFT JOIN gourmet g ON i.content_id = g.id
LEFT JOIN user u ON i.user_id = u.id


        AND i.id = #{id}


        AND i.user_id = #{userId}


        AND i.content_id = #{contentId}


        AND i.type = #{type}


        AND i.create_time BETWEEN #{startTime} AND #{endTime}


ORDER BY i.create_time DESC

    LIMIT #{current},#{size}

SELECT COUNT(*)
FROM interaction i


        AND i.id = #{id}


        AND i.user_id = #{userId}


        AND i.content_id = #{contentId}


        AND i.type = #{type}


        AND i.create_time BETWEEN #{startTime} AND #{endTime}

SELECT i.*
FROM interaction i WHERE content_id IN

    #{gourmetId}

AND i.type = #{interactionQueryDto.type}
AND i.create_time BETWEEN #{interactionQueryDto.startTime} AND #{interactionQueryDto.endTime}

SELECT COUNT(*) FROM interaction i  WHERE content_id IN

    #{gourmetId}

AND i.type = #{type}

SELECT COUNT(*)
FROM interaction i
WHERE i.type = #{type}
  AND i.user_id != #{userId}
  AND (
      EXISTS (
          SELECT 1 FROM gourmet g
          WHERE g.id = i.content_id
            AND g.user_id = #{userId}
            AND i.content_type = 'gourmet'
      )
      OR
      EXISTS (
          SELECT 1 FROM content_net cn
          WHERE cn.id = i.content_id
            AND cn.user_id = #{userId}
            AND i.content_type = 'content_net'
      )
  )

SELECT COUNT(*)
FROM interaction i
WHERE i.type = #{type}
  AND i.user_id != #{userId}
  AND (
      EXISTS (
          SELECT 1 FROM gourmet g
          WHERE g.id = i.content_id
            AND g.user_id = #{userId}
            AND i.content_type = 'gourmet'
      )
      OR
      EXISTS (
          SELECT 1 FROM content_net cn
          WHERE cn.id = i.content_id
            AND cn.user_id = #{userId}
            AND i.content_type = 'content_net'
      )
  )
